// jQuery selector exercise
// select all divs and give them purple bkgd
$("div").css("background", "purple");
// select class highlight divs and make them 200px wide
$(".highlight").css("width", "200px");
// select id 'third' div and give it orange border
$("#third").css("border", "2px solid orange");
// select only first div and change font color to pink
$("div").first().css("color", "pink");